body.script:
function showPopup(type) {
    document.getElementById('overlay').classList.add('show');
    if (type === 'portfolio') {
        document.getElementById('portfolio-popup').classList.add('show');
    } else if (type === 'social') {
        document.getElementById('social-popup').classList.add('show');
    }
}

function hidePopup() {
    document.getElementById('overlay').classList.remove('show');
    document.getElementById('portfolio-popup').classList.remove('show');
    document.getElementById('social-popup').classList.remove('show');
}

