head.script:
//
(function() {
    try {
        var url = new URL(window.location.href);
        // Cek apakah ada parameter 'm' dan nilainya '1'
        if (url.searchParams.get('m') === '1') {
            // Hapus parameter 'm'
            url.searchParams.delete('m');
            // Update URL di browser tanpa reload
            window.history.replaceState(null, document.title, url.toString());
        }
    } catch (e) {
        // Fallback untuk browser purba (sangat jarang terjadi di 2024+)
        console.log("Browser tidak mendukung URL API");
    }
})();
//

